mapper.select:
select log_time, level, logger, line, log_id, data
from (
   select log_time, level, logger, line, log_id, data
   from log
   where log_id = #{logId}
) as t
where t.data like CONCAT('%', #{data}, '%')

select log_time, level, logger, line, log_id, data
from (
   select log_time, level, logger, line, log_id, data
   from log
   where log_time >= #{beginTime} and log_time <= #{endTime}
) as t
where t.data like CONCAT('%', #{data}, '%')

select log_time, level, logger, line, log_id, data
from (
   select log_time, level, logger, line, log_id, data
   from log
   where logger = #{logger}
) as t
where t.data like CONCAT('%', #{data}, '%')

select log_time, level, logger, line, log_id, data
from log
where log_id = #{logId}
